// Helper function to search ADA codes
export const searchADACodes = (query, limit = 10) => {
  if (!query || query.length < 1) return [];
  
  const searchTerm = query.toLowerCase().trim();
  
  return ADA_CODES.filter(item => 
    item.code.toLowerCase().includes(searchTerm) ||
    item.description.toLowerCase().includes(searchTerm) ||
    item.category.toLowerCase().includes(searchTerm)
  ).slice(0, limit);
};

// Get ADA code by exact code
export const getADACode = (code) => {
  return ADA_CODES.find(item => item.code.toUpperCase() === code.toUpperCase());
};

// Get all codes by category
export const getADACodesByCategory = (category) => {
  return ADA_CODES.filter(item => item.category === category);
};

// Get all unique categories
export const getADACategories = () => {
  return [...new Set(ADA_CODES.map(item => item.category))];
};

export default ADA_CODES;
